import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "../ExpenseFilter/ExpenseFilter";
import Card from "../UI/Card";
import "./Expenses.css";
import NewExpense from "../NewExpense/NewExpense";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");

  const filterByYear = (selectedYear) => {
    console.log("in expenses.js", selectedYear);
    setFilteredYear(selectedYear);
  };

  return (
    <Card className="expenses">
      <ExpenseFilter selected={filteredYear} onChangeFilter={filterByYear} />
      {props.items.filter(expense => (
          expense.date.getFullYear().toString() ===
          filteredYear)).map(expense => 
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
      )}
    </Card>
  );
};

export default Expenses;
